<?php

namespace App\Http\Controllers\Api;

use App\Http\Controllers\Controller;
use App\Http\Resources\PaymentSystemResource;
use Illuminate\Http\JsonResponse;

class PaymentSystemController extends Controller
{
    public function index(): JsonResponse
    {
        $mockedSystems = [
            [
                "title" => "Robokassa",
                "icon" => "data:image/png;base64,",
                "value" => "robokassa",
                "isActive" => true,
                "isDisabled" => false,
            ],
            [
                "title" => "Payeer",
                "icon" => "https://play-lh.googleusercontent.com/3HYud1cjMaEnaqoaC2lHr-6uMoFJPd0S4gJi0o_9lf7ClbtLS84hb5kcfTHv797--_bS=w240-h480-rw",
                "value" => "payeer",
                "isActive" => false,
                "isDisabled" => false,
            ],
            [
                "title" => "FreeKassa",
                "icon" => "data:image/png;base64,",
                "value" => "freekassa",
                "isActive" => false,
                "isDisabled" => false,
            ],
        ];

        $paymentGateways = PaymentSystemResource::collection($mockedSystems);

        return response()->json([
            'data' => $paymentGateways
        ]);
